const AedosUtility = require("../shared/aedos_shared_methods");

class Aedos extends AedosUtility {
  constructor() {
    super();
  }

  validateText(text) {
    if (!text || typeof text != "string")
      throw new Error("InvalidInput: text is not of valid type");

    if (text.split(" ").length < 2)
      throw new Error("InvalidInput: text to small to be predected");

    if (text.search(/[A-z]/gim) == -1)
      throw new Error("InvalidInput: text can not be all numbers");

    return new AedosUtility(text);
  }
}

module.exports = Aedos;
